Object.Field:
# Validating KM options
<%

/* Validate COMPATIBLE */
if (!(
	"7".equals( odiRef.getOption ("COMPATIBLE") )  ||
	"8".equals( odiRef.getOption ("COMPATIBLE") ) ||
	"9".equals( odiRef.getOption ("COMPATIBLE") ) ||
	"10".equals( odiRef.getOption ("COMPATIBLE") ) ||
	"11".equals( odiRef.getOption ("COMPATIBLE") )
))  {
	out.print( "raise '\nInvalid value for IKM option COMPATIBLE" );
};

%>

declare
    v0 NUMBER;
    v1 NUMBER;
begin
    select
        case when upper(name) = 'COMPATIBLE' then to_number(substr(value, 1, instr(value, '.',1,1)-1)) else null end v0,
        case when upper(name) = 'COMPATIBLE' then to_number(substr(value, instr(value, '.',1,1)+1, instr(value, '.',1,2)-instr(value, '.',1,1)-1)) else null end v1
    into v0, v1
    from v$parameter
    where upper(name) =  'COMPATIBLE';
    if v0 < <%=odiRef.getOption( "COMPATIBLE" )%> then
        raise_application_error(-20101, 'You are using RDBMS version '||v0||'.'||v1||'. This version does not support IKM COMPATIBLE= <%=odiRef.getOption( "COMPATIBLE" )%>');
    end if;
end;
