Object.Field:
DECLARE
 p_table      VARCHAR2(30)   DEFAULT '<%=odiRef.getTargetTable("TABLE_NAME")%>';
 p_schema     VARCHAR2(30)   DEFAULT '<%=odiRef.getTargetTable("SCHEMA")%>';

 loc          NUMBER;
 ctr          NUMBER;

 o_index             VARCHAR2(32767);                                           -- DDL string used for the original index info
 o_const             VARCHAR2(32767);                                           -- DDL string used for the original const info
 o_refcn             VARCHAR2(32767);                                           -- DDL string used for the original ref-c info
 o_grant             VARCHAR2(32767);                                           -- DDL string used for the original grant info

 v_index             VARCHAR2(32767);                                           -- DDL string used for the new index info
 v_const             VARCHAR2(32767);                                           -- DDL string used for the new const info
 v_refcn             VARCHAR2(32767);                                           -- DDL string used for the new ref-c info
 v_grant             VARCHAR2(32767);                                           -- DDL string used for the new grant info

 v_txt2              VARCHAR2(32767);                                           -- DDL string used for creating indexes
 v_txt3              VARCHAR2(32767);                                           -- DDL string used for creating constraints
 v_txt4              VARCHAR2(32767);                                           -- DDL string used for creating ref-const
 v_txt5              VARCHAR2(32767);                                           -- DDL string used for creating grants

 already_not_null    EXCEPTION;
 PRAGMA EXCEPTION_INIT(already_not_null, -01442);

BEGIN
     BEGIN                                                                      -- Fetch the dependent indexes.
          SELECT DBMS_METADATA.GET_DEPENDENT_DDL('INDEX',       p_table,p_schema) 
            INTO o_index
            FROM dual; 
     EXCEPTION WHEN OTHERS THEN NULL;
     END;

     v_index := REPLACE(o_index, '"."',  '"."N$_');                             -- Add the new "N$_" prefix
     v_index := REPLACE(REPLACE(v_index,'LOGGING', 'NOLOGGING'), 'NONOLOGGING','NOLOGGING');
     ctr := 1;
     loc := 1;
     LOOP
         v_txt2 := SUBSTR(v_index, INSTR(v_index,'CREATE',1,ctr),  
                        (CASE WHEN INSTR(v_index,'LOCAL', 1,ctr) > 0
                              THEN ABS(INSTR(v_index,'CREATE',1,ctr + 1) - INSTR(v_index,'LOCAL', 1,ctr)) - 4
                              WHEN INSTR(v_index,'CREATE',1,ctr + 1) > 0
                              THEN ABS(INSTR(v_index,'CREATE',1,ctr + 1) - INSTR(v_index,'CREATE',1,ctr)) 
                              ELSE 10000
                         END) );

         EXIT WHEN v_txt2 IS NULL;                                                             
         EXECUTE IMMEDIATE v_txt2;                                              -- Create the temp table indexes
         ctr := ctr + 1;
         loc := INSTR(v_index,'CREATE',1,ctr);
         EXIT WHEN loc = 0;
     END LOOP;

     ----

     BEGIN                                                                      -- Fetch the dependent constraints.
          SELECT DBMS_METADATA.GET_DEPENDENT_DDL('CONSTRAINT',  p_table,p_schema) 
            INTO o_const
            FROM dual; 
     EXCEPTION WHEN OTHERS THEN NULL;
     END;

     v_const := REPLACE(REPLACE(o_const, '"."',  '"."N$_'), 
                                   'CONSTRAINT "','CONSTRAINT "N$_');           -- Add the new "N$_" prefix
     ctr := 1;
     LOOP
         v_txt3 := SUBSTR(v_const, INSTR(v_const,'ALTER',1,ctr),  
                        (CASE WHEN INSTR(v_const,'ALTER',1,ctr + 1) > 0
                              THEN INSTR(v_const,'ALTER',1,ctr + 1) - INSTR(v_const,'ALTER',1,ctr) 
                              ELSE 10000
                         END) );

         EXIT WHEN v_txt3 IS NULL;      
         BEGIN                                                         
             EXECUTE IMMEDIATE v_txt3;                                          -- Create the temp table constraints
         EXCEPTION 
             WHEN already_not_null THEN NULL;
             WHEN others           THEN RAISE;
         END;
         ctr := ctr + 1;
         loc := INSTR(v_const,'ALTER',1,ctr);
         EXIT WHEN loc = 0;
     END LOOP;

     ----

     BEGIN                                                                      -- Fetch the dependent ref constraints.
          SELECT DBMS_METADATA.GET_DEPENDENT_DDL('REF_CONSTRAINT',p_table,p_schema)  
            INTO o_refcn
            FROM dual; 
     EXCEPTION WHEN OTHERS THEN NULL;
     END; 

     v_refcn := REPLACE(REPLACE(o_refcn, '"'||p_table||'"', '"N$_'||p_table||'"'), 
                                   'CONSTRAINT "','CONSTRAINT "N$_');           -- Add the new "N$_" prefix
     ctr := 1;

     LOOP
         v_txt4 := SUBSTR(v_refcn, INSTR(v_refcn,'ALTER',1,ctr),  
                        (CASE WHEN INSTR(v_refcn,'ALTER',1,ctr + 1) > 0
                              THEN ABS(INSTR(v_refcn,'ALTER',1,ctr + 1) - INSTR(v_refcn,'ALTER',1,ctr))
                              ELSE 10000
                         END) );

         EXIT WHEN v_txt4 IS NULL;                                                    
         EXECUTE IMMEDIATE v_txt4;                                              -- Create the temp table referential cons
         ctr := ctr + 1;
         loc := INSTR(v_refcn,'ALTER',1,ctr);
         EXIT WHEN loc = 0;
     END LOOP;

END;

begin
 execute immediate 'LOCK TABLE <%=odiRef.getTable("L", "TARG_NAME", "A")%> PARTITION  (<%=odiRef.getOption("APARTITION_VARIABLE")%>)  IN SHARE MODE';
end;

begin
   execute immediate 'ALTER TABLE <%=odiRef.getTable("L", "TARG_NAME", "A")%> EXCHANGE PARTITION <%=odiRef.getUserExit("APARTITION_VARIABLE")%> WITH TABLE <%=odiRef.getTargetTable("SCHEMA")%>.N$_<%=odiRef.getTargetTable("RES_NAME")%> INCLUDING INDEXES WITHOUT VALIDATION UPDATE GLOBAL INDEXES';
end;
